sys_ui_action.client_script_v2:
function onClick(g_form) {
	var spend = new GlideAjax('x_snc_adventureman.spend');
	spend.addParam('sysparm_name', 'buy');
	spend.addParam('sysparm_sys_id', g_form.getUniqueValue());
	spend.addParam('sysparm_item_index', '5');
	spend.getXML(ResponseFunction);

	function ResponseFunction(response){
		var answer = response.responseXML.documentElement.getAttribute("answer"); 
		var results = JSON.parse(answer);
		g_form.addErrorMessage(results.message);
	}
}

sys_ui_action.script:
function goBuy(){
	var spend = new GlideAjax('x_snc_adventureman.spend');
	spend.addParam('sysparm_name', 'buy');
	spend.addParam('sysparm_sys_id', g_form.getUniqueValue());
	spend.addParam('sysparm_item_index', '5');
	spend.getXML(ResponseFunction);

	function ResponseFunction(response){
		var answer = response.responseXML.documentElement.getAttribute("answer"); 
		var results = JSON.parse(answer);
		g_form.addErrorMessage(results.message);
	}
	return;
}
